mapper.select:
SELECT count(*) FROM product 
WHERE smallcategory_name = #{smallcategory_name} 
and (s_stock>0 or M_STOCK>0 or L_STOCK>0 or XL_STOCK>0)

SELECT p.product_number,p.product_name,p.image,p.price,p.content,p.temperature, p.s_stock,p.m_stock,p.l_stock,p.xl_stock,p.smallcategory_name, COUNT(od.product_number) AS product_count, AVG(r.rating) AS average_rating FROM product p INNER JOIN category c ON p.smallcategory_name = c.smallcategory_name LEFT JOIN orderdetail od ON p.product_number = od.product_number LEFT JOIN review r ON od.orderdetail_number = r.orderdetail_number where 1=1
and product_name like #{keyword}
and c.bigcategory_name = #{bigcategory_name}
and p.smallcategory_name = #{smallcategory_name}
and p.product_name like #{brand}
GROUP BY p.product_number,p.product_name,p.image,p.price,p.content,p.temperature, p.s_stock,p.m_stock,p.l_stock,p.xl_stock,p.smallcategory_name
order by p.product_number desc
order by p.price, product_number desc
order by p.price desc, product_number desc
order by product_count DESC, product_number desc
order by average_rating desc nulls last, product_number desc

SELECT count(*) FROM product 
WHERE product_name like #{brand} 
and (s_stock>0 or M_STOCK>0 or L_STOCK>0 or XL_STOCK>0)

SELECT p.product_number,p.product_name,p.image,p.price,p.content,p.temperature, p.s_stock,p.m_stock,p.l_stock,p.xl_stock,p.smallcategory_name, COUNT(od.product_number) AS product_count, AVG(r.rating) AS average_rating FROM product p INNER JOIN category c ON p.smallcategory_name = c.smallcategory_name LEFT JOIN orderdetail od ON p.product_number = od.product_number LEFT JOIN review r ON od.orderdetail_number = r.orderdetail_number
where c.bigcategory_name = #{bigcategory_name}
where p.smallcategory_name = #{smallcategory_name}
where p.product_name like #{brand}
GROUP BY p.product_number,p.product_name,p.image,p.price,p.content,p.temperature, p.s_stock,p.m_stock,p.l_stock,p.xl_stock,p.smallcategory_name
order by p.product_number desc
order by p.price, product_number desc
order by p.price desc, product_number desc
order by product_count DESC, product_number desc
order by average_rating desc nulls last, product_number desc

mapper.update:
update product 
set s_stock=0,m_stock=0,l_stock=0,xl_stock=0
where product_number=#{product_number}

update product 
set product_name=#{product_name}, image=#{image}, price=#{price}, content=#{content}, temperature=#{temperature},
s_stock=#{s_stock}, m_stock=#{m_stock}, l_stock=#{l_stock}, xl_stock=#{xl_stock}, smallcategory_name=#{smallcategory_name}
where product_number=#{product_number}
